import React, { useState } from 'react';
import Staffnavbar from "./Staffnavbar";

const AddCourse = () => {
  const[email,setEmail]=useState("");
  const[password,setPassword]=useState("");
  const[test,setTest]=useState("");
  let testlist=["Html","CSS","JavaScript","Python","React","Django","Flask","Angular","Express"]
  let handleLogin=()=>{
    alert(test);
  }
  return (
  <>
    <Staffnavbar/>
    <div className='form-wrapper'>
    <h3 style={{color:"#28a745", textAlign:"center",fontSize:"2vw",padding:'0px',margin:'0px',fontWeight:"500"}}>Add Course</h3>
         <p className='form-label'>Course Name</p>
         <input className='course-form-input' type='text' value={email} onChange={(event=>setEmail(event.target.value))}/>
         <p className='form-label'>Description</p>
         <textarea rows = "5" className='course-form-textfield' type='password' value={password} onChange={(event=>setPassword(event.target.value))}/>
         <p className='form-label'>Choose test</p>
         <select placeholder="Begin typing a name to filter..." className='course-form-select' type='text'  value={test} onChange={(event=>setTest(event.target.value))}>
           {/* <option className='course-form-option'></option>
           <option className='course-form-option'>CSS</option>
           <option className='course-form-option'>SCRIPT</option>
           <option className='course-form-option'>COM</option>
           <option className='course-form-option'>jQuery</option>
           <option className='course-form-option'>HTML5</option> */}
           {testlist.map((element) => (<>
           <option>{element}</option>
           </>))}
         </select>
         <button className='form-button' onClick={handleLogin}>Create</button>
    </div>
  </>
  )
}

export default AddCourse
