import React from 'react';
import '../styles/Coursecard.css';

const QuestionCard = ({Question, Option1, Option2, Option3, Option4}) => {
  return (
    <div className="coursecard-wrapper">
    <p className='coursecard-desc'><b>Question:</b> {Question}</p>
    {/* <p className='coursecard-desc' style={{marginBottom:"5px",marginTop:"5px"}}><b>A:</b> {Option1}</p>
    <p className='coursecard-desc' style={{marginBottom:"5px",marginTop:"5px"}}><b>B:</b> {Option2}</p>
    <p className='coursecard-desc' style={{marginBottom:"5px",marginTop:"5px"}}><b>C:</b> {Option3}</p>
    <p className='coursecard-desc' style={{marginBottom:"5px",marginTop:"5px"}}><b>D:</b> {Option4}</p> */}

    <div style={{textAlign:"left"}}>
        <input type="radio" value="Male" name="gender" /> {Option1}<br></br>
        <input type="radio" value="Female" name="gender" /> {Option2}<br></br>
        <input type="radio" value="Other" name="gender" /> {Option3}<br></br>
        <input type="radio" value="Other" name="gender" /> {Option4}<br></br>
    </div>
    </div>
  )
}

export default QuestionCard
