    }
    }

    let usableid=data[0].Student_id;
    let testid=Number(test.testid)
       let submit=()=>{
      console.log(score);
      axios({
        method:"post",
        url:"http://localhost:8000/courseapi/score/",
        data:{
          Score:score,
          Test_id:testid,
          Student_id:usableid
        }
    }).then((res)=>{
      alert("Submitted Successfully");
    }).catch((err)=>{
      alert("Error.");
    })

  }

    let getuser=()=>{
      dispatch(profileLoading())
        axios({
          method: "post",
          url: 'http://localhost:8000/managerapi/getloggedinstudent/',
          data:{
            Email:token.Email,
            Password:token.Password
            }
      }).then((response)=>{
        dispatch(profileSuccess(response.data));
      }).catch((error)=>{
        dispatch(profileError());
      })
      }

    let getQuestions=()=>{
        dispatch(questionLoading());
          axios({
            method:"post",
            url:"http://localhost:8000/courseapi/getquestions/",
            data:{
              Test_id:test.testid
            }
          }).then((res)=>{
            dispatch(questionSuccess(res.data.data));
          }).catch((err)=>{
            dispatch(questionError());
          })
        }
        useEffect(()=>{
          getQuestions();
          getuser();
        },[])
  return (
    <>
    {qlist? (<>
    {qlist.map((ele)=>(
        <>
         <div className="coursecard-wrapper">
    <p className='coursecard-desc'><b>Question:</b> {ele.Question}</p>
    <div style={{textAlign:"left"}} onChange={(e)=>setAnswer(e.target.value)}>
        <input type="radio" value={ele.Option1} name="gender" /> {ele.Option1}<br></br>
        <input type="radio" value={ele.Option2} name="gender" /> {ele.Option2}<br></br>
        <input type="radio" value={ele.Option3} name="gender" /> {ele.Option3}<br></br>
        <input type="radio" value={ele.Option4} name="gender" /> {ele.Option4}<br></br>
    </div>
    <button id="btn" className='profile-button' onClick={()=>{evaluate(ele.Correct_Ans)}}>Lock</button>
    </div>
        {/* <QuestionCard {...ele} key={ele.Question_id}/>
         */}
        </>
    ))}
    <div style={{width:"max-content",marginLeft:"auto",marginRight:"auto"}}>
    <button className='card-button' style={{width:"max-content",marginLeft:"auto",marginRight:"auto"}} onClick={submit}>Submit</button>
    </div></>):(<><p>No Questions Assigned.</p></>)}
    </>
  )
}

