import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { getcourseError, getcourseLoading, getcourseSuccess } from '../../redux/staff/Actions';
import Staffnavbar from './Staffnavbar'
const StaffCourse = () => {

  const dispatch=useDispatch();
  const {data}=JSON.parse(sessionStorage.staffid);
  let id=data[0].Staff_id;
  const {courses}=useSelector(state=>state.staff.getcourse);
  let handlefetch=()=>{
    dispatch(getcourseLoading())
      axios({
        method: "post",
        url: 'http://localhost:8000/courseapi/getcourses/',
        data:{
          Author:id,
        }
    }).then((response)=>{
      dispatch(getcourseSuccess(response.data));
    }).catch((error)=>{
      dispatch(getcourseError());
      alert(error);
    })
    }
    useEffect(()=>{
      handlefetch();
    },[])
  return (
    <>
    <Staffnavbar/>
    <div>

    </div>
    </>
  )
}

export default StaffCourse
