import axios from 'axios';
import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { getcourseError, getcourseLoading, getcourseSuccess } from '../../redux/staff/Actions';
import Staffnavbar from './Staffnavbar'
const StaffCourse = () => {

  const dispatch=useDispatch();
  const {data}=JSON.parse(sessionStorage.staffid);
  let id=data[0].Staff_id;
  const {courses}=useSelector(state=>state.staff.getcourse);
  console.log(courses);
  let handlefetch=()=>{
    dispatch(getcourseLoading())
      axios({
        method: "post",
        url: 'http://localhost:8000/courseapi/getcourses/',
        data:{
          Author:id,
        }
    }).then((response)=>{
      dispatch(getcourseSuccess(response.data));
    }).catch((error)=>{
      dispatch(getcourseError());
      alert(error);
    })
    }
    useEffect(()=>{
      handlefetch();
    },[])
  return (
    <>
    <Staffnavbar/>
    <div style={{marginTop:"3vw",display:"grid",width:"95%",marginLeft:"auto",marginRight:"auto",
    position:"relative",gridTemplateColumns:"repeat(3, 1fr)"}} >
        {courses.map((ele)=>(<div id="card" style={{width:"90%", padding:"1vw", border:"1px solid #28a745", marginLeft:"auto", 
        marginRight:"auto",marginBottom:"1.5vw"}} key={ele.Author}>

        </div>))}
    </div>
    </>
  )
}

export default StaffCourse
