import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { subscriptionError, subscriptionLoading, subscriptionSuccess } from '../../redux/student/Actions';
import Studentnavbar from './Studentnavbar'

const StudentSubscriptions = () => {
  // const id=JSON.parse(localStorage.data);
  const [array,setArray] = useState([]);
  const {data}=useSelector(state=>state.student.profile);
  let usableid=(data[0].Student_id);
  let dispatch=useDispatch();

  let getsubs=(x)=>{
    dispatch(subscriptionLoading())
    axios({
      method:"get",
      url:`http://localhost:8000/courseapi/course/${x}/`,
    }).then((res)=>{
      setArray(prev => [res.data,...prev]);
      dispatch(subscriptionSuccess(res.data));
    }).catch((err)=>{
      dispatch(subscriptionError());
      console.log(err);
    })
  }
  let fechSubs=()=>{
    axios({
      method:"post",
      url:"http://localhost:8000/courseapi/getsubscribed/",
      data:{
        Student:usableid
      }
    }).then((res)=>{
      (res.data.data).map((ele)=>{
        getsubs(ele.Course);

      })
    }).catch((err)=>{
      console.log(err);
    })
  }
  useEffect(()=>{
  fechSubs();
  },[])
  console.log(data[0].Student_id);
  return (
    <>
    <Studentnavbar/>
    <h3 style={{color:"#28a745", textAlign:"center",fontSize:"1.5vw",padding:'0px',marginTop:'2vw',fontWeight:"500"}}>Your Subscriptions</h3>
    {array.map((ele)=>(
      <p>{ele.Name}</p>
    ))}
    </>
  )
}

export default StudentSubscriptions
