import React, { useState } from 'react'
import Studentnavbar from './Studentnavbar'
import '../styles/Forms.css';

const StudentLogin = () => {
  const[email,setEmail]=useState("");
  const[password,setPassword]=useState("");
  const[confirmpass,setConformpass]=useState("");

  //inputfield class variables
  let emailClass ={border:"1.5px solid red"};
  let passswordClass="form-input";
  let confirmClass="form-input";
  let target=document.getElementById("email")
  //login function
  let handleLogin=()=>{
    let emailcheck= /\S+@\S+\.\S+/;
    if(!email){
      alert("Please enter email.");
      // emailClass={border:"1.5px solid red"};
      alert(target);
    }
    else if(!emailcheck.test(email)){
      alert("Invalid email.");
    }
    else if(!password){
      alert("Please enter password.");
    }
    else if(!confirmpass){
      alert("Please confirm password.")
    }
    else if(password!==confirmpass){
      alert("Password and Confirm passsword should be same.");
    }
    else{
    alert("Success.");
    }
  }
  return (
    <>
    <Studentnavbar/>
    <div className='form-wrapper'>
    <h3 style={{color:"#28a745", textAlign:"center",fontSize:"2.5vw",padding:'0px',margin:'0px',fontWeight:"500"}}>Login form</h3>
         <p className='form-label'>Email</p>
         <input id="email" className="form-input" type='text' value={email} onChange={(event=>setEmail(event.target.value))}/>
         <p className='form-label'>Password</p>
         <input className={passswordClass} type='password' value={password} onChange={(event=>setPassword(event.target.value))}/>
         <p className='form-label'>Confirm Password</p>
         <input className={confirmClass} type='password' value={confirmpass} onChange={(event=>setConformpass(event.target.value))}/>
         <button className='form-button' onClick={handleLogin}>Login</button>
    </div>
    </>
  )
}

export default StudentLogin
