import * as React from 'react';
import '../styles/Forms.css';
import '../styles/Profile.css'
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import '../styles/Forms.css';

const style = {
  position: 'absolute',
  top: '15%',
  left: '50%',
  transform: 'translate(-50%)',
  width:'85%',
  bgcolor: 'background.paper',
  border: '1px solid #28a745;',
  boxShadow: 24,
  textAlign:'center',
  p: 4,
};

export default function AddTest({course_id}) {
  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  return (
    <div>
      <button onClick={handleOpen} className='profile-button'>Add Test</button>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
         <h3 style={{color:"#28a745",fontSize:"2vw",fontWeight:"400",textAlign:"center"}}>Add Test</h3>
         <p className='form-label'>Test name</p>
         <input id="email" className='course-form-input' type='text'/>
         <div style={{width:"100%",borderTop:".5px solid grey",marginTop:"1.5vw"}}></div>
         <h3 style={{color:"#28a745",fontSize:"1.5vw",fontWeight:"400"}}>Add Questions</h3>
         <p className='form-label'>Question 1</p>
         <textarea rows = "2" className='course-form-textfield' placeholder="500 characters maximum...." type='text' />
        </Box>
      </Modal>
    </div>
  );
}
