import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { profileError, profileLoading, profileSuccess } from '../../redux/student/Actions';
import '../styles/Coursecard.css';

const StudentCourseCard = ({Name,Description,Author,Course_id}) => {
const[author,setAuthor]=useState(null);
const token=JSON.parse(sessionStorage.student_token);
let dispatch=useDispatch();
const {data}=useSelector(state=>state.student.profile);


console.log(data);
  let handleSubscribe=()=>{
  axios({
      method: "post",
      url: 'http://localhost:8000/courseapi/assign/',
      data:{
           Course:Course_id,
           Student:"dhbsbd"
      }
  }).then((res)=>{
  // setAuthor(response.data.Name)
  console.log(res)
  }).catch((error)=>{
  console.log(error);
  })
  }


  let getauthor=()=>{
      axios({
        method: "get",
        url: `http://localhost:8000/managerapi/staff/${Author}/`,
    }).then((response)=>{
    setAuthor(response.data.Name)
    }).catch((error)=>{
    console.log(error);
    })
    }
    useEffect(()=>{
      getauthor();
    },[])


    let getuser=()=>{
      dispatch(profileLoading())
        axios({
          method: "post",
          url: 'http://localhost:8000/managerapi/getloggedinstudent/',
          data:{
            Email:token.Email,
            Password:token.Password
            }
      }).then((response)=>{
        dispatch(profileSuccess(response.data));
      }).catch((error)=>{
        dispatch(profileError());
        let errmessage=error.response.data.Email;
        alert("Error :"+errmessage);
      })
      }
      getuser();

  return (
  <>
  <div className="coursecard-wrapper">
    <h2 className='coursecard-title'>{Name}</h2>
    <button style={{width:"max-content",margin:"0px",color:"grey",fontWeight:"600",fontSize:"1vw",backgroundColor:"white",border:"1px solid"}} disabled>Author:{author}</button>
    <p className='coursecard-desc'><b>Description:</b>{Description}</p>
    {/* <button style={{width:"max-content",margin:"0px",color:"grey",fontWeight:"600",fontSize:"1vw",backgroundColor:"white",border:"1px solid"}} disabled>Author:{author}</button> */}
    <button className='card-button' onClick={handleSubscribe}>Subscribe</button>
    </div>
  </>
  )
}

export default StudentCourseCard
