import * as React from 'react';
import '../styles/Forms.css';
import '../styles/Profile.css'
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import '../styles/Forms.css';

const style = {
  position: 'absolute',
  top: '15%',
  left: '50%',
  transform: 'translate(-50%)',
  width:'maxContent',
  bgcolor: 'background.paper',
  border: '1px solid #28a745;',
  boxShadow: 24,
  textAlign:'center',
  p: 4,
};

export default function AddTest({course_id, name}) {
  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  const [tname, setTname]=React.useState("");

  let addTest=()=>{
 alert(tname);
 handleClose();
  }
  return (
    <div>
      <button onClick={handleOpen} className='profile-button'>Add Test</button>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
         <h3 style={{color:"#28a745",fontSize:"2vw",fontWeight:"400",textAlign:"center"}}>Add Test for {name}</h3>
         <p className='form-label' style={{textAlign:"center"}}>Test name</p>
         <input id="email" className='course-form-input'
          style={{marginLeft:"auto",marginRight:"auto"}} type='text'
          value={tname}
          onChange={(ele)=>setTname(ele.target.value)}
          />
         <button className='form-button' onClick={addTest}>Add</button>
        </Box>
      </Modal>
    </div>
  );
}
